
public class Switch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

			int num=0;
			switch(num) {
			case 0:
				System.out.println("number is 0");
				//break;
			case 1:
				System.out.println("number is 1");
				//break;
			default:
				System.out.println(num);
			}
	}

}
