package Wednesday210922;

public class TestSubstring {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
			String s="Sachin Tendulkar";
			System.out.println("Original String:\t\t"+s);
			System.out.println("Substring starting from index 6:\t"+s.substring(6));//Tendulkar
			System.out.println("Substring starting from index 0 to 6:\t"+s.substring(0,6));//Sachin
	}

}
