package Wednesday210922;

public class TestSubstring1 {

		public static void main(String[] args) {
			// TODO Auto-generated method stub
				String s="000012345";
				System.out.println("Original String:\t\t"+s);
				System.out.println("Substring starting from index 5:\t"+s.substring(4));
				System.out.println("Substring starting from index 0 to 4:\t"+s.substring(0,4));
		}

	}

